const { Telegraf } = require("telegraf");
const TOKEN = "6456398897:AAEP2j09JLgqluudftxWMSxDujmFT4sws6g"; // Replace with your actual bot token
const bot = new Telegraf(TOKEN);

const web_link = "https://celebrated-torte-184681.netlify.app/";

bot.start((ctx) =>
  ctx.reply(
    "Welcome! How can I assist you today?",
    createMainMenu()
  )
);

// Handle the "web app" button click
bot.hears("web app", (ctx) =>
  ctx.reply("Here is the link to the web app:", {
    reply_markup: {
      inline_keyboard: [
        [{ text: "Open Web App", url: web_link }],
        [{ text: "View Cart", callback_data: "view_cart" }],
      ],
    },
  })
);

// Handle the "view_cart" callback
bot.action("view_cart", (ctx) => {
  // Replace this with your logic to send the user's cart information
  const cartMessage = "Your shopping cart:\n..."; // Include the actual cart information
  ctx.reply(cartMessage);
});

// Add more handlers for additional functionality as needed

bot.launch();

function createMainMenu() {
  return {
    reply_markup: {
      keyboard: [
        [{ text: "Web App", callback_data: "web app" }],
        // Add more buttons as needed
      ],
      resize_keyboard: true,
    },
  };
}
